            var ts = initTmStatus(0, 1, 1, 'Лампочка');

            function update() {
            	if (isTmStatusUnreliable(ts)) {
            		setElementProperty('lamp', 'fill', 'white');
            	}
            	else if (isTmStatusOn(ts)) {
            		setElementProperty('lamp', 'fill', 'green');
            	}
				else {
            		setElementProperty('lamp', 'fill', 'red');
            	}
            }

            return update;
